function calcCenaticAffinityRisk( sources ) {
var n_of_lic = program.getScenario().getConstraint( "NLicenses", "st" );
var n_of_eq_lic = program.getScenario().getConstraint( "NEqualLicenses", "st" );
var n_of_comp_lic = program.getScenario().getConstraint( "NCompatibleLicenses", "st" );
var n_of_lic_ty = program.getScenario().getConstraint( "NLicenseTypes", "st" );

var n1 = Number(n_of_eq_lic) / Number(n_of_lic);
var n2 = Number(n_of_comp_lic) / Number(n_of_lic);
var n3_1 = (Number(n_of_lic_ty) + Number(1));
var n3_2 = (n3_1 / Number(n_of_lic)) * 100;
var n3 = (1 / n3_2 );

var r = 1 - ( Number(n1) + Number( n2 * n3) );

return fx.e( r, 0 );
}

function atLeastOne( sources ) {
var i;
for( i = 0; i < sources.size(); i++ ) {
  if( sources.get(i).getPositive() > 0 ) {
    return fx.e( 1, 0 );
    }
  }
return fx.e( 0, 0 );
}

var n_of_eq_lic = program.getScenario().getConstraint( "NEqualLicenses", "st" );
var n_of_comp_lic = program.getScenario().getConstraint( "NCompatibleLicenses", "st" );

program.getScenario().setConstraint( "NLicenses", "st", Number(n_of_eq_lic) + Number(n_of_comp_lic) );
